package me.lovethefeel.order.api;

import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.http.MediaType;
import org.springframework.test.web.reactive.server.WebTestClient;

import static org.springframework.boot.test.context.SpringBootTest.WebEnvironment.*;

@SpringBootTest(webEnvironment = RANDOM_PORT)
class OrderIntegrationTest {

    @Autowired
    private WebTestClient webTestClient;

    @DisplayName("헤더의 길이가 8KB 초과하면 익셉션")
    @Test
    void exceed_header_size() {

        final long 주문번호 = 1L;
        final String header_길이_8193_byte = "";

        this.webTestClient.get().uri("/order/find/"+주문번호)
                .header("User-Member-Id", header_길이_8193_byte)
                .accept(MediaType.APPLICATION_JSON)
                .exchange()
                .expectStatus().isBadRequest();
    }
}